import React from 'react';
import { LineChart, Line, XAxis, Tooltip } from 'recharts';
import './AverageSessionsChart.css';

const daysOfWeek = ['L', 'M', 'M', 'J', 'V', 'S', 'D'];  

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${payload[0].value} min`}</p>
      </div>
    );
  }
  return null;
};

const AverageSessionsChart = ({ data }) => {
  return (
    <div className="chart-container-average">
      <div className="chart-header">
        <h2>Dur√©e moyenne des <br />
          sessions</h2>
      </div>
      <LineChart
        width={'auto'}
        height={150}
        data={data}
        margin={{ top: 5, right: 10, bottom: 5, left: 10 }}
      >
        <XAxis
          dataKey="day"
          tickFormatter={(tick) => daysOfWeek[tick - 1]}
          tick={{ fill: 'rgba(255, 255, 255, 1), rgba(255, 255, 255, 0.4032)' }}
          axisLine={false}
          tickLine={false}
        />
        <Tooltip content={<CustomTooltip />} />
        <Line
          type="monotone"
          dataKey="sessionLength"
          stroke="rgba(255, 255, 255, 1),rgba(255, 255, 255, 0.4032)"
          strokeWidth={2}
          dot={false}
          connectNulls={true}
        />
      </LineChart>
    </div>
  );
};

export default AverageSessionsChart;
