import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './ActivityChart.css';

// Прилагодена функција за икона во легендата
const renderLegend = (props) => {
  const { payload } = props;
  if (!payload) return null; // Додадена проверка

  return (
    <ul className="custom-legend">
      {payload.map((entry, index) => (
        <li key={`item-${index}`} style={{ listStyleType: 'none', margin: '0 10px' }}>
          <span className="legend-icon" style={{ backgroundColor: entry.color }}></span>
          {entry.value}
        </li>
      ))}
    </ul>
  );
};

const ActivityChart = ({ data }) => {
  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
        <div className="legend-container">
          <Legend content={renderLegend} verticalAlign="top" height={36} />
        </div>
      </div>
      <div className="chart">
        <BarChart width={835} height={320} data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="day" />
          <YAxis />
          <Tooltip />
          <Bar dataKey="kilogram" stackId="a" fill="black" name="Poids (kg)" barSize={7} radius={[10, 10, 0, 0]} />
          <Bar dataKey="calories" stackId="b" fill="red" name="Calories brûlées (kCal)" barSize={7} radius={[10, 10, 0, 0]} />
        </BarChart>
      </div>
    </div>
  );
};

export default ActivityChart;
