const BASE_URL = 'https://projet-12-oc.vercel.app';

// Fonction pour récupérer les données utilisateur
export async function fetchUserData(userId) {
  try {
    const response = await fetch(`${BASE_URL}/users/${userId}`);
    if (!response.ok) {
      throw new Error('Réponse du réseau non valide.');
    }
    const userData = await response.json();
    return mapUserData(userData);  // Formatage des données
  } catch (error) {
    console.error('Erreur lors de la récupération des données utilisateur :', error);
    throw error;
  }
}

// Fonction pour récupérer l'activité utilisateur
export async function fetchUserActivity(userId) {
  try {
    const response = await fetch(`${BASE_URL}/users/${userId}/activity`);
    if (!response.ok) {
      throw new Error('Réponse du réseau non valide.');
    }
    const userActivity = await response.json();
    return mapUserActivity(userActivity);  // Formatage des données
  } catch (error) {
    console.error('Erreur lors de la récupération de l\'activité utilisateur :', error);
    throw error;
  }
}

// Fonction pour récupérer les sessions moyennes de l'utilisateur
export async function fetchUserAverageSessions(userId) {
  try {
    const response = await fetch(`${BASE_URL}/users/${userId}/average-sessions`);
    if (!response.ok) {
      throw new Error('Réponse du réseau non valide.');
    }
    const userAverageSessions = await response.json();
    return mapUserAverageSessions(userAverageSessions);  // Formatage des données
  } catch (error) {
    console.error('Erreur lors de la récupération des sessions moyennes de l\'utilisateur :', error);
    throw error;
  }
}

// Fonction pour récupérer les performances de l'utilisateur
export async function fetchUserPerformance(userId) {
  try {
    const response = await fetch(`${BASE_URL}/users/${userId}/performance`);
    if (!response.ok) {
      throw new Error('Réponse du réseau non valide.');
    }
    const userPerformance = await response.json();
    return mapUserPerformance(userPerformance);  // Formatage des données
  } catch (error) {
    console.error('Erreur lors de la récupération des performances de l\'utilisateur :', error);
    throw error;
  }
}

// Fonctions de mapping pour formater les données reçues

function mapUserData(userData) {
  return {
    id: userData.id,
    userInfos: {
      firstName: userData.firstName,
      lastName: userData.lastName,
    },
    keyData: {
      calorieCount: userData.calorieCount,
      proteinCount: userData.proteinCount,
      carbohydrateCount: userData.carbohydrateCount,
      lipidCount: userData.lipidCount,
    },
    score: userData.score,
  };
}

function mapUserActivity(userActivity) {
  return {
    userId: userActivity.userId,
    sessions: userActivity.sessions.map(session => ({
      date: session.date,
      duration: session.duration,
    })),
  };
}

function mapUserAverageSessions(userAverageSessions) {
  return {
    userId: userAverageSessions.userId,
    sessions: userAverageSessions.sessions.map(session => ({
      date: session.date,
      averageDuration: session.averageDuration,
    })),
  };
}

function mapUserPerformance(userPerformance) {
  return {
    userId: userPerformance.userId,
    data: {
      speed: userPerformance.speed,
      strength: userPerformance.strength,
      endurance: userPerformance.endurance,
    },
  };
}
