// apiService.js

const BASE_URL = 'https://api.github.com';

// Function to fetch user data
export async function fetchUserData(username) {
  try {
    const response = await fetch(`${BASE_URL}/users/${username}`);
    if (!response.ok) {
      throw new Error('Network response was not ok.');
    }
    const userData = await response.json();
    return mapUserData(userData); // Data standardization
  } catch (error) {
    console.error('Error fetching user data:', error);
    throw error;
  }
}

// Other fetch functions for user activity, average sessions, and performance...

// Mapping functions for data standardization
function mapUserData(userData) {
  return {
    id: userData.id,
    userInfos: {
      username: userData.login,
      avatarUrl: userData.avatar_url,
      profileUrl: userData.html_url,
      name: userData.name,
      location: userData.location,
      bio: userData.bio,
      publicRepos: userData.public_repos,
      followers: userData.followers,
      following: userData.following,
    },
  };
}

// Other mapping functions for user activity, average sessions, and performance...
