import React from 'react';
import {  Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const AverageSessionsChart = ({ data }) => {
  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Sessions Moyennes</h2>
      </div>
      <div className="chart">
        <BarChart
          width={835}
          height={320}
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="day" label={{ value: 'Jour', position: 'insideBottom', offset: -5 }} />
          <YAxis label={{ value: 'Sessions', angle: -90, position: 'insideLeft' }} />
          <Tooltip />
          <Legend verticalAlign="top" height={36} />
          <Bar
            dataKey="sessionLength"
            fill="blue"
            name="DurÃ©e de session (min)"
            barSize={7}
          />
          <Line
            type="monotone"
            dataKey="average"
            stroke="red"
            name="Moyenne"
            dot={false}
          />
        </BarChart>
      </div>
    </div>
  );
};

export default AverageSessionsChart;
