// apiService.js
import { getUserData, getUserActivity, getUserAverageSessions, getUserPerformance } from '../data'; // Prilagodete go patot kako shto e potrebno

export const fetchUserData = async (userId) => {
  try {
    const userData = await getUserData(userId);
    return userData;
  } catch (error) {
    console.error('Error fetching user data:', error);
    throw error;
  }
};

export const fetchUserActivity = async (userId) => {
  try {
    const userActivity = await getUserActivity(userId);
    return userActivity;
  } catch (error) {
    console.error('Error fetching user activity:', error);
    throw error;
  }
};

export const fetchUserAverageSessions = async (userId) => {
  try {
    const userAverageSessions = await getUserAverageSessions(userId);
    return userAverageSessions;
  } catch (error) {
    console.error('Error fetching user average sessions:', error);
    throw error;
  }
};

export const fetchUserPerformance = async (userId) => {
  try {
    const userPerformance = await getUserPerformance(userId);
    return userPerformance;
  } catch (error) {
    console.error('Error fetching user performance:', error);
    throw error;
  }
};
