import React from 'react';

const NutritionalInfo = ({ calorieCount, proteinCount, carbohydrateCount }) => {
  // Define paths to icons
  const caloriesIconPath = '../data/icons/calories-icon.png';
  const proteinsIconPath = '../data/icons/proteins-icon.png';
  const carbohydratesIconPath = '../data/icons/carbohydrates-icon.png';

  return (
    <div className="nutrition-container">
      <div className="nutrition-item">
        <div className="icon calories-icon" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}${caloriesIconPath})` }}></div>
        <div className="text">
          <span className="amount">{calorieCount}kCal</span>
          <span className="label">Calories</span>
        </div>
      </div>
      <div className="nutrition-item">
        <div className="icon proteins-icon" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}${proteinsIconPath})` }}></div>
        <div className="text">
          <span className="amount">{proteinCount}g</span>
          <span className="label">Prot√©ines</span>
        </div>
      </div>
      <div className="nutrition-item">
        <div className="icon carbohydrates-icon" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}${carbohydratesIconPath})` }}></div>
        <div className="text">
          <span className="amount">{carbohydrateCount}g</span>
          <span className="label">Glucides</span>
        </div>
      </div>
    </div>
  );
}

export default NutritionalInfo;
