import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './header.css';
import logo from '../../images/logo/logo.png';

const Header = () => {
  const location = useLocation();

  return (
    <nav className="horizontal-nav">
      <ul>
        <li><img src={logo} alt="logo" className="logo" /></li>
        <li className={location.pathname === '/' ? 'active' : ''}><Link to="/">Accueil</Link></li>
        <li className={location.pathname === '/profile' ? 'active' : ''}><Link to="/profile">Profil</Link></li>
        <li className={location.pathname === '/settings' ? 'active' : ''}><Link to="/settings">Réglage</Link></li>
        <li className={location.pathname === '/community' ? 'active' : ''}><Link to="/community">Communauté</Link></li>
      </ul>
    </nav>
  );
};

export default Header;
