import React from 'react';
import './NutritionalInfo.css';

const NutritionalInfo = ({ calorieCount, proteinCount, carbohydrateCount, lipidCount }) => {
  return (
    <div className="nutritional-info-container">
      <div className="nutritional-info">
        <p>Calories</p>
        <p>{calorieCount}kCal</p>
      </div>
      <div className="nutritional-info">
        <p>Prot√©ines</p>
        <p>{proteinCount}g</p>
      </div>
      <div className="nutritional-info">
        <p>Glucides</p>
        <p>{carbohydrateCount}g</p>
      </div>
      <div className="nutritional-info">
        <p>Lipides</p>
        <p>{lipidCount}g</p>
      </div>
    </div>
  );
};

export default NutritionalInfo;
