import axios from 'axios';

const BASE_URL = 'http://localhost:3000/user/1';

export const fetchUserData = async (userId) => {
    try {
        const response = await axios.get(`${BASE_URL}/user/${userId}`);
        return response.data;
    } catch (error) {
        console.error('Error fetching user data:', error);
        throw error;
    }
};

export const fetchUserActivity = async (userId) => {
    try {
        const response = await axios.get(`${BASE_URL}/user/${userId}/activity`);
        return response.data;
    } catch (error) {
        console.error('Error fetching user activity:', error);
        throw error;
    }
};

export const fetchUserAverageSessions = async (userId) => {
    try {
        const response = await axios.get(`${BASE_URL}/user/${userId}/average-sessions`);
        return response.data;
    } catch (error) {
        console.error('Error fetching average sessions:', error);
        throw error;
    }
};

export const fetchUserPerformance = async (userId) => {
    try {
        const response = await axios.get(`${BASE_URL}/user/${userId}/performance`);
        return response.data;
    } catch (error) {
        console.error('Error fetching user performance:', error);
        throw error;
    }
};
