import React, { useState, useEffect } from 'react';
import './Profile.css';
import VerticalNav from '../components/VerticalNav/VerticalNav';
import ScoreChart from '../components/charts/ScoreChart/ScoreChart'; // Uvezite ScoreChart komponentu
import {
  fetchUserData,
  fetchUserActivity,
  fetchUserAverageSessions,
  fetchUserPerformance,
} from '../service/api'; // Uvezite svoje funkcije za dohvaćanje podataka
import mockData from '../mockData.json'

const Profile = () => {
  const [selectedProfile, setSelectedProfile] = useState('score'); // Početno odabrani profil
  const [userId, setUserId] = useState('18'); // Default userId, adjust as needed
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Dohvati podatke o korisniku na temelju trenutnog userId-a
        const fetchedUserData = await fetchUserData(userId);
        const fetchedUserActivity = await fetchUserActivity(userId);
        const fetchedUserAverageSessions = await fetchUserAverageSessions(userId);
        const fetchedUserPerformance = await fetchUserPerformance(userId);

        setUserData(fetchedUserData);
        setUserActivity(fetchedUserActivity);
        setUserAverageSessions(fetchedUserAverageSessions);
        setUserPerformance(fetchedUserPerformance);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);

        // Postavi podatke iz mockData ako dohvaćanje ne uspije
        setUserData(mockData.USER_MAIN_DATA.find(user => user.id === parseInt(userId)) || null);
        setUserActivity(mockData.USER_ACTIVITY.find(activity => activity.userId === parseInt(userId)) || null);
        setUserAverageSessions(mockData.USER_AVERAGE_SESSIONS.find(session => session.userId === parseInt(userId)) || null);
        setUserPerformance(mockData.USER_PERFORMANCE.find(performance => performance.userId === parseInt(userId)) || null);
      }
    };

    fetchData();
  }, [userId]);

  const handleProfileChange = (profileType) => {
    setSelectedProfile(profileType); // Ažuriraj odabrani profil
  };

  if (loading || !userData || !userActivity || !userAverageSessions || !userPerformance) {
    return <div>Loading...</div>; // Stanje učitavanja
  }

  if (!userActivity.sessions || !userAverageSessions.sessions || !userPerformance.data) {
    return <div>Data not available</div>; // Rukovanje nedostupnim podacima
  }

  const { firstName } = userData.userInfos;

  // Možete ukloniti scoreData varijablu ako više nije potrebna
  // let scoreData;
  // if (selectedProfile === 'score') {
  //   scoreData = userData.score; // Prikaži score podatke
  // } else if (selectedProfile === 'todayScore') {
  //   scoreData = userPerformance.todayScore; // Prikaži todayScore podatke
  // }

  return (
    <div className="profile-container">
      <VerticalNav onProfileChange={handleProfileChange} /> {/* Proslijedi funkciju za promjenu profila */}
      <div className="profile-header-section">
        <div className="profile-header">
          <h1>Hello <span className="user-name">{firstName}</span></h1>
          <p>Congratulations! You smashed your goals yesterday 👏</p>
        </div>
        {/* Prikaz ScoreChart komponente sa odgovarajućim podacima */}
        <ScoreChart
          data={selectedProfile === 'score' ? userData.score : userPerformance.todayScore}
        />
      </div>
    </div>
  );
};

export default Profile;
