import React from 'react';
import { BarChart, Bar, CartesianGrid, Tooltip, Legend, XAxis, YAxis } from 'recharts';
import './ActivityChart.css';

const ActivityChart = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>Loading...</div>; // Gérer l'état de chargement ou le scénario de données vides
  }

  const days = data.map(entry => entry.day); // Obtenez les jours à partir des données

  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
      </div>
      <div className="chart">
        <BarChart
          width={800}
          height={400}
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 30 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="day" label={{ value: 'Jour', position: 'insideBottom' }} ticks={days} />
          <YAxis
            domain={[69, 'auto']} 
            tickFormatter={(tick) => `${tick} kg`} // Formater les étiquettes avec 'kg'
            label={{ value: 'kg', angle: -90, position: 'insideRight' }}
          />
          <Tooltip />
          <Legend verticalAlign="top" height={36} />
          <Bar dataKey="kilogram" stackId="a" fill="black" name="Poids (kg)" barSize={20} />
          <Bar dataKey="calories" stackId="b" fill="red" name="Calories brûlées (kCal)" barSize={20} />
        </BarChart>
      </div>
    </div>
  );
};

export default ActivityChart;
