import React, { useState, useEffect } from 'react';
import { BarChart, Bar, CartesianGrid, Tooltip, Legend, XAxis, YAxis } from 'recharts';
import './ActivityChart.css';

const ActivityChart = () => {
  const [data, setData] = useState(null); // State za čuvanje podataka

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/datas/userActivityData.json'); // Putanja do JSON fajla u public folderu
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const days = [1, 2, 3, 4, 5, 6, 7]; // Definiranje brojeva dana

  if (!data) {
    return <div>Loading...</div>; // Prikazivanje poruke za učitavanje dok se podaci učitavaju
  }

  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
      </div>
      <div className="chart">
        <BarChart
          width={800}
          height={400}
          data={data} // Koristimo učitane podatke iz userActivity.json fajla
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="day" ticks={days} label={{ value: 'Jour', position: 'insideBottom' }} />
          <YAxis label={{ value: 'kg', angle: -90, position: 'insideLeft' }} />
          <Tooltip />
          <Legend verticalAlign="top" height={36} />
          <Bar dataKey="kilogram" stackId="a" fill="black" name="Poids (kg)" barSize={30} />
          <Bar dataKey="calories" stackId="b" fill="red" name="Calories brûlées (kCal)" barSize={30} />
        </BarChart>
      </div>
    </div>
  );
};

export default ActivityChart;
