import React, { useState, useEffect } from 'react';
import './Profile.css';
import ActivityChart from '../charts/ActivityChart/ActivityChart';
import AverageSessionsChart from '../charts/AverageSessionsChart/AverageSessionsChart';
import NutritionalInfo from '../charts/NutritionalInfo/NutritionalInfo';
import RadarChart from '../charts/PerformanceChart/RadarChart';
import ScoreChart from '../charts/ScoreChart/ScoreChart';

// Importing mock data (adjust the path as needed)
import mockData from '../../mockData.json';

const Profile = ({ userId }) => {
  // State hooks for storing user data
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  // useEffect to fetch user data
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Simulating fetching data from an API or using mock data
        // Replace with your actual fetching logic
        await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate loading delay

        // Example mock data usage
        const mockUserData = mockData.USER_MAIN_DATA.find(user => user.id === parseInt(userId));
        const activityData = mockData.USER_ACTIVITY.find(activity => activity.userId === parseInt(userId));
        const averageSessionsData = mockData.USER_AVERAGE_SESSIONS.find(session => session.userId === parseInt(userId));
        const performanceData = mockData.USER_PERFORMANCE.find(performance => performance.userId === parseInt(userId));

        if (mockUserData && activityData && averageSessionsData && performanceData) {
          setUserData({
            userInfos: {
              firstName: mockUserData.userInfos.firstName,
            },
            keyData: {
              calorieCount: mockUserData.keyData.calorieCount,
              proteinCount: mockUserData.keyData.proteinCount,
              carbohydrateCount: mockUserData.keyData.carbohydrateCount,
              lipidCount: mockUserData.keyData.lipidCount,
            },
            score: mockUserData.score,
            activity: activityData.sessions,
            averageSessions: averageSessionsData.sessions,
            performance: performanceData.data,
          });
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false); // Set loading to false once data fetching is complete
      }
    };

    fetchUserData();
  }, [userId]);

  // If data is still loading, display a loading message
  if (loading || !userData) {
    return <div>Loading...</div>;
  }

  // Destructure values for easier access in the JSX
  const { firstName } = userData.userInfos;

  return (
    <div className="profile-container">
      <div className="profile-header-section">
        <div className="profile-header">
          <h1>Bonjour <span className="user-name">{firstName}</span></h1>
          <p>F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
        </div>
        <div className="section-activity">
          <ActivityChart data={userData.activity} />
        </div>
        <div className="trois-sections">
          <div className="section">
            <AverageSessionsChart data={userData.averageSessions} />
          </div>
          <div className="section">
            <RadarChart data={userData.performance} />
          </div>
          <div className="section">
            <ScoreChart data={{ score: userData.score }} />
          </div>
        </div>
      </div>
      <div className="sections">
        <NutritionalInfo
          calorieCount={userData.keyData.calorieCount}
          proteinCount={userData.keyData.proteinCount}
          carbohydrateCount={userData.keyData.carbohydrateCount}
          lipidCount={userData.keyData.lipidCount}
        />
      </div>
    </div>
  );
};

export default Profile;
