import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/header/header';
import VerticalNav from './components/VerticalNav/VerticalNav';
import DailyActivityChart from './components/DailyActivityChart/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart/AverageSessionsChart';
import ActivityTypeChart from './components/ActivityTypeChart/ActivityTypeChart';
import AverageScoreChart from './components/AverageScoreChart/AverageScoreChart';
import KeyInfoCard from './components/KeyInfoCards/KeyInfoCards';
import { fetchUserData, fetchUserActivity, fetchUserAverageSessions, fetchUserPerformance } from './services/api';

const App = () => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState([]);
  const [userAverageSessions, setUserAverageSessions] = useState([]);
  const [userPerformance, setUserPerformance] = useState([]);
  
  useEffect(() => {
    const getData = async () => {
      const userId = 1;  // Replace with actual user ID
      const user = await fetchUserData(userId);
      const activity = await fetchUserActivity(userId);
      const averageSessions = await fetchUserAverageSessions(userId);
      const performance = await fetchUserPerformance(userId);

      setUserData(user);
      setUserActivity(activity.sessions);
      setUserAverageSessions(averageSessions.sessions);
      setUserPerformance(performance.data);
    };

    getData();
  }, []);

  if (!userData) return <div>Loading...</div>;

  return (
    <div className="App">
      <Header />
      <VerticalNav />
      <header className="App-header">
        <h1>Bonjour {userData.firstName}</h1>
      </header>
      <div className="charts-container">
        <DailyActivityChart data={userActivity} />
        <AverageSessionsChart data={userAverageSessions} />
        <ActivityTypeChart data={userPerformance} />
        <AverageScoreChart data={[{ score: userData.todayScore }]} />
        <div className="key-info-cards">
          <KeyInfoCard icon="icon1" label="Calories" value={`${userData.keyData.calorieCount} kcal`} />
          <KeyInfoCard icon="icon2" label="Proteins" value={`${userData.keyData.proteinCount} g`} />
          <KeyInfoCard icon="icon3" label="Carbs" value={`${userData.keyData.carbohydrateCount} g`} />
          <KeyInfoCard icon="icon4" label="Fat" value={`${userData.keyData.lipidCount} g`} />
        </div>
      </div>
    </div>
  );
};

export default App;
