import React, { useEffect, useState } from 'react';
import { fetchUserData } from '../../services/api';
import SessionChart from '../SessionChart/SessionChart';
import CaloriesChart from '../CaloriesChart/CaloriesChart';
import AverageSessionChart from '../AverageSessionsChart/AverageSessionsChart';
import PerformanceChart from '../PerformanceChart/PerformanceChart';
import KeyInfoCards from '../KeyInfoCards/KeyInfoCards';
import { formatSessionData, formatCaloriesData, formatAverageSessionsData, formatPerformanceData } from '../../uttils/dataFormatter';

const Profile = () => {
  const [sessions, setSessions] = useState([]);
  const [calories, setCalories] = useState([]);
  const [user, setUser] = useState(null);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    async function getData() {
      try {
        const fetchedUserData = await fetchUserData();
        if (fetchedUserData) {
          setUserData(fetchedUserData);
          setUser(fetchedUserData.userInfos.firstName);
          setSessions(formatSessionData(fetchedUserData.sessions));
          setCalories(formatCaloriesData(fetchedUserData.calories));
          // Fetch and set average sessions and performance here
        } else {
          console.error('No user data received.');
          // Handle the case where fetchUserData() returns null or undefined
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        // Handle error fetching user data
      }
    }
    getData();
  }, []);

  // Render loading state if userData is not yet fetched
  if (!userData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Bonjour {user}</h1>
      <SessionChart data={sessions} />
      <CaloriesChart data={calories} />
      <AverageSessionChart data={formatAverageSessionsData(userData.averageSessions)} />
      <PerformanceChart data={formatPerformanceData(userData.performance)} />
      <KeyInfoCards data={userData.keyData} />
    </div>
  );
};

export default Profile;
