import React, { useEffect, useState } from 'react';
import { fetchUserData, fetchUserActivity, fetchUserAverageSessions, fetchUserPerformance } from '../../services/api';
import SessionChart from '../SessionChart/SessionChart';
import CaloriesChart from '../CaloriesChart/CaloriesChart';
import AverageSessionsChart from '../AverageSessionsChart/AverageSessionsChart';
import PerformanceChart from '../PerformanceChart/PerformanceChart';
import KeyInfoCards from '../KeyInfoCards/KeyInfoCards';

const Profile = ({ userId }) => {
    const [user, setUser] = useState(null);
    const [activity, setActivity] = useState(null);
    const [averageSessions, setAverageSessions] = useState([]);
    const [performance, setPerformance] = useState([]);
  
    useEffect(() => {
      async function getData() {
        try {
          const userData = await fetchUserData(userId);
          console.log('User:', userData); // Debug
          setUser(userData);
          
          const userActivity = await fetchUserActivity(userId);
          console.log('Activity:', userActivity); // Debug
          setActivity(userActivity);
          
          const userAverageSessions = await fetchUserAverageSessions(userId);
          console.log('Average Sessions:', userAverageSessions); // Debug
          setAverageSessions(userAverageSessions);
          
          const userPerformance = await fetchUserPerformance(userId);
          console.log('Performance:', userPerformance); // Debug
          setPerformance(userPerformance);
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      }
      getData();
    }, [userId]);
  
    if (!user || !activity || !averageSessions.length || !performance.length) {
      return <div>Loading...</div>;
    }
  
    return (
      <div>
        <h1>Profil Utilisateur</h1>
        {user && <h2>Bienvenue, {user.userInfos.firstName}</h2>}
        <SessionChart data={activity.sessions} />
        <CaloriesChart data={activity.calories} />
        <KeyInfoCards data={activity.keyData} />
        <AverageSessionsChart data={averageSessions} />
        <PerformanceChart data={performance} />
      </div>
    );
  };
  
  export default Profile;
