import React, { useEffect, useState } from 'react';
import './Profile.css';
import ActivityChart from '../charts/ActivityChart/ActivityChart';
import AverageSessionsChart from '../charts/AverageSessionsChart/AverageSessionsChart';
import { fetchUserData, fetchUserActivity, fetchUserAverageSessions, fetchIconPaths } from '../fetchFunctions/fetchFunctions'; // Adjust import based on your actual function

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [iconPaths, setIconPaths] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data1 = await fetchUserData(userId);
        const data2 = await fetchUserActivity(userId);
        const data3 = await fetchUserAverageSessions(userId);
        const paths = await fetchIconPaths(); // Assuming this function fetches paths to icons

        setUserData(data1);
        setUserActivity(data2);
        setUserAverageSessions(data3);
        setIconPaths(paths); // Set fetched icon paths into state
      } catch (error) {
        console.error('Error fetching data:', error);
        // Handle error state if needed
      }
    };

    fetchData();
  }, [userId]);

  if (!userData || !userActivity || !userAverageSessions || !iconPaths) return <div>Loading...</div>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>Bonjour <span className="user-name">{userData.userInfos.firstName}</span></h1>
        <p>F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
      </div>

      <div className="section">
        <ActivityChart data={userActivity.sessions} />
      </div>
      <div className="section">
        <AverageSessionsChart data={userAverageSessions.sessions} />
      </div>

      <div className="section">
        <NutritionalInfo
          calorieCount={userData.nutritionalInfo.calorieCount}
          proteinCount={userData.nutritionalInfo.proteinCount}
          carbohydrateCount={userData.nutritionalInfo.carbohydrateCount}
          iconPaths={iconPaths} // Pass fetched icon paths as props
        />
      </div>
    </div>
  );
};

export default Profile;
