import React, { useEffect, useState } from 'react';
import './Profile.css';
import ActivityChart from '../charts/ActivityChart/ActivityChart';
import AverageSessionsChart from '../charts/AverageSessionsChart/AverageSessionsChart'; // Uvoz AverageSessionsChart
import { fetchUserData, fetchUserActivity, fetchAverageSessionLength } from '../fetchFunctions/fetchFunctions'; // Pretpostavka da postoji funkcija za dohvat proseƒçne du≈æine sesija

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [averageSessionData, setAverageSessionData] = useState(null); // State za proseƒçne podatke o sesijama

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Dohvati korisniƒçke podatke i podatke o aktivnostima
        const userDataResponse = await fetchUserData(userId);
        const userActivityResponse = await fetchUserActivity(userId);

        // Postavi stanje sa podacima
        setUserData(userDataResponse);
        setUserActivity(userActivityResponse);

        // Dohvati proseƒçnu du≈æinu sesija
        const averageSessionResponse = await fetchAverageSessionLength(userId);

        // Postavi stanje sa proseƒçnim podacima o sesijama
        setAverageSessionData(averageSessionResponse);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [userId]);

  // Prika≈æi Loader dok se podaci uƒçitavaju
  if (!userData || !userActivity || !averageSessionData) {
    return <div>Loading...</div>;
  }

  // Prikaz profila sa podacima
  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>Bonjour <span className="user-name">{userData.userInfos.firstName}</span></h1>
        <p>F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
      </div>

      <div className="section">
        <ActivityChart data={userActivity.sessions} />
      </div>

      <div className="section">
        <AverageSessionsChart data={averageSessionData} /> {/* Prikaz AverageSessionsChart */}
      </div>
    </div>
  );
};

export default Profile;
