import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

const ScoreChart = ({ data }) => {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);
  const textRef = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    const remainingScore = 1 - data;

    chartInstanceRef.current = new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: ['Remaining', 'Achieved'],
        datasets: [{
          data: [remainingScore * 100, data * 100],
          backgroundColor: ['rgba(255, 255, 255, 0.2)', 'rgba(255, 0, 0, 1)'],
          borderColor: ['rgba(255, 255, 255, 0.2)', 'rgba(255, 0, 0, 1)'],
          borderWidth: 1
        }]
      },
      options: {
        cutout: '80%',
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            enabled: false
          },
          title: {
            display: true,
            text: 'Score',
            font: { weight: 'bold', size: 16 },
            position: 'top',
            padding: 10,
            lineHeight: 1.5,
            align: 'start',
            anchor: 'start',
            offset: {
              y: 60
            }
          },
          backgroundPlugin: {
            id: 'backgroundPlugin',
            beforeDraw: (chart) => {
              const { ctx, chartArea } = chart;
              ctx.save();
              ctx.fillStyle = 'rgba(200, 200, 200, 0.5)'; // Gray background color with opacity
              ctx.fillRect(chartArea.left, chartArea.top, chartArea.width, chartArea.height);
              ctx.restore();
            }
          }
        }
      }
    });

    if (textRef.current) {
      textRef.current.innerHTML = `<span style="color: black">${data * 100}%</span> <br/><span style="color: rgba(116, 121, 140, 1)">de votre</span> <br/><span style="color: rgba(116, 121, 140, 1)">objectif</span>`;
    }

    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [data]);

  return (
    <div style={{ position: 'relative', width: '100%', maxWidth: '400px', height: '100%', maxHeight: '400px', margin: '0 auto' }}>
      <canvas ref={chartRef} style={{ width: '100%', height: '100%' }} />
      <div ref={textRef} style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        fontFamily: 'Arial, sans-serif',
        fontSize: '26px',
        fontWeight: 'bold',
        textAlign: 'center',
        lineHeight: '1.2'
      }}></div>
    </div>
  );
};

export default ScoreChart;
