    throw error;
  }
}

export async function fetchUserPerformance(userId) {
  try {
    const response = await fetch(`${BASE_URL}/${userId}/performance`)

  } catch (error) {
    console.error('Error fetching user performance:', error);
    throw error;
  }
}
