// ActivityChart.js
import React from 'react';
import { BarChart, Bar, CartesianGrid, Tooltip, Legend } from 'recharts';
import XAxisWrapper from '../../Axis/XAxisWrapper';
import YAxisWrapper from '../../Axis/YAxisWrapper';

const ActivityChart = ({ data }) => {
  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
      </div>
      <div className="chart">
        <BarChart
          width={800}
          height={400}
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxisWrapper dataKey="day" label={{ value: 'Jour', position: 'insideBottom' }} />
          <YAxisWrapper label={{ value: 'Valeurs', angle: -90, position: 'insideLeft' }} />
          <Tooltip />
          <Legend verticalAlign="top" height={36} />
          <Bar dataKey="kilogram" stackId="a" fill="black" name="Poids (kg)" barSize={30} />
          <Bar dataKey="calories" stackId="b" fill="red" name="Calories brûlées (kCal)" barSize={30} />
        </BarChart>
      </div>
    </div>
  );
};

export default ActivityChart;
