import React from 'react';
import { LineChart, Line, XAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './AverageSessionsChart.css';

const daysOfWeek = ['L', 'M', 'M', 'J', 'V', 'S', 'D']; // Nedelje: 1, 2, 3, 4, 5, 6, 7

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${payload[0].value} min`}</p>
      </div>
    );
  }
  return null;
};

const AverageSessionsChart = ({ data }) => {
  return (
    <div className="chart-container-average">
      <div className="chart-header">
        <h2>Durée moyenne des sessions</h2>
      </div>
      <ResponsiveContainer width="100%" height={150}>
        <LineChart
          data={data}
          margin={{ top: 5, right: 30, left:20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" vertical={false} />
          <XAxis
            dataKey="day"
            tickFormatter={(tick) => daysOfWeek[tick - 1]}
            tick={{ fill: '#ffffff' }}
            axisLine={false}
            tickLine={false}
          />
          <Tooltip content={<CustomTooltip />} />
          <Line
            type="monotone"
            dataKey="sessionLength"
            stroke="#ffffff"
            strokeWidth={2}
            dot={{ stroke: '#ffffff', strokeWidth: 2, fill: 'red', r: 4 }}
            activeDot={{ r: 8 }}
            connectNulls={true} // Ovo omogućava povezivanje null vrednosti u podacima
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default AverageSessionsChart;
