import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import {
  PolarAngleAxis,
  PolarGrid,
  Radar,
  RadarChart,
  ResponsiveContainer,
} from 'recharts';
import { styleVar } from '../../utils/style/styleVariables';

const ACTIVITIES_ORDER_IN_CHART = [
  'Intensité',
  'Vitesse',
  'Force',
  'Endurance',
  'Energie',
  'Cardio',
];

export function ActivitiesChart({ userId, mockData }) {
  const activities = mockData.map(item => ({
    activity: ACTIVITIES_ORDER_IN_CHART[item.kind - 1],
    value: item.value,
  }));

  const orderedActivities = ACTIVITIES_ORDER_IN_CHART.map(activity => 
    activities.find(item => item.activity === activity) || { activity, value: 0 }
  );

  return (
    <ActivitiesChartContainer>
      <ResponsiveContainer width="100%" height="100%">
        <RadarChart
          data={orderedActivities}
          outerRadius={window.innerWidth > 1340 ? '70%' : '60%'}
        >
          <PolarGrid radialLines={false} />
          <PolarAngleAxis
            dataKey="activity"
            stroke="white"
            dy={4}
            tickLine={false}
            tick={{
              fontSize: 12,
              fontWeight: 500,
            }}
          />
          <Radar
            dataKey="value"
            fill={`${styleVar.primary500}`}
            fillOpacity={0.7}
            stroke="transparent"
          />
        </RadarChart>
      </ResponsiveContainer>
    </ActivitiesChartContainer>
  );
}

ActivitiesChart.propTypes = {
  userId: PropTypes.string.isRequired,
  mockData: PropTypes.array.isRequired,
};

const ActivitiesChartContainer = styled.div`
  background: ${styleVar.neutral800};
`;
