import React, { useEffect, useState } from 'react';
import './Profile.css';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import {
  fetchUserData,
  fetchUserActivity,
  fetchUserAverageSessions,
  fetchUserPerformance,
} from '../fetchFunctions/fetchFunctions';

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data1 = await fetchUserData(userId);
      const data2 = await fetchUserActivity(userId);
      const data3 = await fetchUserAverageSessions(userId);
      const data4 = await fetchUserPerformance(userId);
      setUserData(data1);
      setUserActivity(data2);
      setUserAverageSessions(data3);
      setUserPerformance(data4);
    };

    fetchData();
  }, [userId]);

  if (!userData || !userActivity || !userAverageSessions || !userPerformance) return <div>Loading...</div>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>{userData.userInfos.firstName}</h1>
        <p>Age: {userData.userInfos.age}</p>
      </div>

      <div className="section">
        <h2>User Activity (Calories)</h2>
        <div className="chart-container">
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={userActivity.sessions}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="day" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="calories" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Add other sections for Average Sessions and Performance as needed */}
    </div>
  );
};

export default Profile;
