import React from 'react';
import { BarChart, Bar, CartesianGrid, Tooltip, Legend, XAxis, YAxis, ResponsiveContainer } from 'recharts';
import './ActivityChart.css';

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${payload[0].value}kg`}</p>
        <p className="label">{`${payload[1].value}kCal`}</p>
      </div>
    );
  }

  return null;
};

const ActivityChart = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>Loading...</div>; // Gérer l'état de chargement ou le scénario de données vides
  }

  const days = data.map(entry => entry.day); // Obtenez les jours à partir des données

  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
        <Legend 
          verticalAlign="top" 
          align="right"
          payload={[
            { value: 'Poids (kg)', type: 'circle', id: 'ID01', color: 'black' },
            { value: 'Calories brûlées (kCal)', type: 'circle', id: 'ID02', color: 'red' }
          ]}
        />
      </div>
      <div className="chart">
        <ResponsiveContainer width="100%" height={400}>
          <BarChart
            data={data}
            margin={{ top: 20, right: 30, left: 20, bottom: 30 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="day" 
              label={{ value: 'Jour', position: 'insideBottom', offset: -5 }} 
              ticks={days} 
              tick={{ fontSize: 12 }} 
              tickLine={false}
            />
            <YAxis
              domain={[69, 71]} // Définir le domaine de l'axe Y pour afficher de 69 à 71
              interval={0} // Afficher toutes les étiquettes dans le domaine
              tickFormatter={(tick) => `${tick}`} // Formater les étiquettes sans 'kg'
              label={{ value: 'kg', angle: -90, position: 'insideLeft', offset: -5 }} // Étiquette de l'axe Y
              tick={{ fontSize: 12 }}
              tickLine={false}
            />
            <Tooltip content={<CustomTooltip />} />
            <Bar dataKey="kilogram" fill="black" name="Poids (kg)" barSize={10} radius={[3, 3, 0, 0]} />
            <Bar dataKey="calories" fill="red" name="Calories brûlées (kCal)" barSize={10} radius={[3, 3, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ActivityChart;
