import React, { useState, useEffect } from 'react';
import './Profile.css';
import ActivityChart from '../charts/ActivityChart/ActivityChart';
import AverageSessionsChart from '../charts/AverageSessionsChart/AverageSessionsChart';
import NutritionalInfo from '../charts/NutritionalInfo/NutritionalInfo';
import RadarChart from '../charts/PerformanceChart/RadarChart';
import ScoreChart from '../charts/ScoreChart/ScoreChart';

// Importing mock data (adjust the path as needed)
import mockData from '../../mockData.json';

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserDataFromApi = async () => {
      try {
        setLoading(true);
        setError(null);

        // Simulating data fetch delay
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Assuming API fetch logic, replace with your actual API call
        const response = await fetch(`api/users/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const userDataFromApi = await response.json();

        // Set user data from API response
        setUserData({
          userInfos: {
            firstName: userDataFromApi.userInfos.firstName,
          },
          keyData: {
            calorieCount: userDataFromApi.keyData.calorieCount,
            proteinCount: userDataFromApi.keyData.proteinCount,
            carbohydrateCount: userDataFromApi.keyData.carbohydrateCount,
            lipidCount: userDataFromApi.keyData.lipidCount,
          },
          score: userDataFromApi.score,
        });

        // Set user activity, average sessions, and performance from API or fallback to mock data
        setUserActivity(userDataFromApi.activity);
        setUserAverageSessions(userDataFromApi.averageSessions);
        setUserPerformance(userDataFromApi.performance);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data from API:', error);
        setError(error.message);
        setLoading(false);

        // If API fails, fallback to mock data
        fetchUserDataFromMock();
      }
    };

    const fetchUserDataFromMock = () => {
      try {
        setLoading(true);

        // Simulate data fetch delay
        setTimeout(() => {
          const mockUserData = mockData.USER_MAIN_DATA.find(user => user.id === parseInt(userId));

          const mockData = {
            userInfos: {
              firstName: 'Cecilia',
            },
            keyData: {
              calorieCount: 2500,
              proteinCount: 90,
              carbohydrateCount: 150,
              lipidCount: 120,
            },
            score: 85,
            activity: mockData.USER_ACTIVITY.find(activity => activity.userId === parseInt(userId)).sessions,
            averageSessions: mockData.USER_AVERAGE_SESSIONS.find(session => session.userId === parseInt(userId)).sessions,
            performance: mockData.USER_PERFORMANCE.find(performance => performance.userId === parseInt(userId)).data,
          };

          setUserData(mockUserData);
          setUserActivity(mockData.activity);
          setUserAverageSessions(mockData.averageSessions);
          setUserPerformance(mockData.performance);

          setLoading(false);
        }, 1000); // Simulate 1 second delay
      } catch (error) {
        console.error('Error fetching mock user data:', error);
        setError('Failed to fetch user data');
        setLoading(false);
      }
    };

    fetchUserDataFromApi();
  }, [userId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  // Render profile UI with fetched data
  return (
    <div className="profile-container">
      <div className="profile-header-section">
        <div className="profile-header">
          <h1>Bonjour <span className="user-name">{userData.userInfos.firstName}</span></h1>
          <p>Félicitation ! Vous avez explosé vos objectifs hier 👏</p>
        </div>
        <div className="section-activity">
          <ActivityChart data={userActivity} />
        </div>
        <div className="trois-sections">
          <div className="section">
            <AverageSessionsChart data={userAverageSessions} />
          </div>
          <div className="section">
            <RadarChart data={userPerformance} />
          </div>
          <div className="section">
            <ScoreChart data={{ score: userData.score }} />
          </div>
        </div>
      </div>
      <div className="sections">
        <NutritionalInfo
          calorieCount={userData.keyData.calorieCount}
          proteinCount={userData.keyData.proteinCount}
          carbohydrateCount={userData.keyData.carbohydrateCount}
          lipidCount={userData.keyData.lipidCount}
        />
      </div>
    </div>
  );
};

export default Profile;
