import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Import Axios for making HTTP requests

const Profile = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [averageSessions, setAverageSessions] = useState([]);
  const [performance, setPerformance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch user data from backend using Axios
        const userDataResponse = await axios.get(`http://localhost:3000/user/${userId}`);
        const userData = userDataResponse.data.data; // Assuming your data is wrapped in 'data' key

        // Fetch user activity data
        const activityResponse = await axios.get(`http://localhost:3000/user/${userId}/activity`);
        const activityData = activityResponse.data.data;

        // Fetch average sessions data
        const averageSessionsResponse = await axios.get(`http://localhost:3000/user/${userId}/average-sessions`);
        const averageSessionsData = averageSessionsResponse.data.data;

        // Fetch performance data
        const performanceResponse = await axios.get(`http://localhost:3000/user/${userId}/performance`);
        const performanceData = performanceResponse.data.data;

        // Set state with fetched data
        setUser(userData.userInfos);
        setSessions(activityData.sessions);
        setAverageSessions(averageSessionsData.sessions);
        setPerformance(performanceData.data);
        setLoading(false); // Update loading state
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData(); // Call the fetch function

    // Cleanup function (optional)
    return () => {
      // Cleanup logic if needed
    };
  }, [userId]); // Dependency on userId ensures data refreshes when userId changes

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Bonjour {user.firstName}</h1>
      {/* Render your charts/components using fetched data */}
      {/* Example: <SessionChart data={sessions} /> */}
    </div>
  );
};

export default Profile;
