import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const ActivityChart = ({ data }) => {
  return (
    <ResponsiveContainer width="100%" height={400}>
      <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="day" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="kilogram" stackId="a" fill="#8884d8" name="Weight (kg)" />
        <Bar dataKey="calories" stackId="b" fill="#82ca9d" name="Calories (cal)" />
        <Legend align="right" verticalAlign="top" height={36} />
        <text x="50%" y="10" textAnchor="middle">kg / cal</text>
      </BarChart>
    </ResponsiveContainer>
  );
};

export default ActivityChart;
