import React, { useEffect, useState } from 'react';
import {
  fetchUserData,
  fetchUserActivity,
  fetchUserAverageSessions,
  fetchUserPerformance,
} from '../fetchFunctions/fetchFunctions';
import { LineChart, Line, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userDataResponse = await fetchUserData(userId);
        setUserData(userDataResponse);

        const userActivityResponse = await fetchUserActivity(userId);
        setUserActivity(userActivityResponse);

        const userAverageSessionsResponse = await fetchUserAverageSessions(userId);
        setUserAverageSessions(userAverageSessionsResponse);

        const userPerformanceResponse = await fetchUserPerformance(userId);
        setUserPerformance(userPerformanceResponse);
      } catch (error) {
        console.error('Error fetching data:', error);
        // Handle error states if needed
      }
    };

    fetchData();
  }, [userId]);

  if (!userData || !userActivity || !userAverageSessions || !userPerformance) {
    return <div>Loading...</div>;
  }

  // Transform userActivity data into a format suitable for Recharts LineChart
  const activityChartData = userActivity[userId].sessions.map(session => ({
    day: session.day, // Assuming session.day is in a suitable format for the x-axis
    calories: session.calories, // Assuming calories should be plotted on the y-axis
  }));

  return (
    <div>
      <h1>{userData.userInfos.firstName}</h1>
      <p>Age: {userData.userInfos.age}</p>

      {/* Render Recharts LineChart for Activity */}
      <h2>Activity</h2>
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={activityChartData}>
          <XAxis dataKey="day" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="calories" stroke="#8884d8" />
        </LineChart>
      </ResponsiveContainer>

      {/* Render Average Sessions */}
      <h2>Average Sessions</h2>
      <pre>{JSON.stringify(userAverageSessions[userId], null, 2)}</pre>

      {/* Render Performance */}
      <h2>Performance</h2>
      <pre>{JSON.stringify(userPerformance[userId], null, 2)}</pre>
    </div>
  );
};

export default Profile;
