import React from 'react';
import caloriesIcon from '../images/data/data-small/calories-icon.svg';
import proteinsIcon from '../images/data/data-small/proteins-icon.svg';
import carbohydratesIcon from '../images/data/data-small/carbohydrates-icon.svg';

const NutritionalInfo = ({ calorieCount, proteinCount, carbohydrateCount }) => {
  return (
    <div className="nutrition-container">
      <div className="nutrition-item">
        <div className="icon calories-icon" style={{ backgroundImage: `url(${caloriesIcon})` }}></div>
        <div className="text">
          <span className="amount">{calorieCount}kCal</span>
          <span className="label">Calories</span>
        </div>
      </div>
      <div className="nutrition-item">
        <div className="icon proteins-icon" style={{ backgroundImage: `url(${proteinsIcon})` }}></div>
        <div className="text">
          <span className="amount">{proteinCount}g</span>
          <span className="label">Prot√©ines</span>
        </div>
      </div>
      <div className="nutrition-item">
        <div className="icon carbohydrates-icon" style={{ backgroundImage: `url(${carbohydratesIcon})` }}></div>
        <div className="text">
          <span className="amount">{carbohydrateCount}g</span>
          <span className="label">Glucides</span>
        </div>
      </div>
    </div>
  );
}

export default NutritionalInfo;
