// UserProfile.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserProfile = ({ userId }) => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/user/${userId}`);
        setUserData(response.data.data); // Pristupamo podacima unutar 'data' objekta
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);

  if (!userData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>User Profile</h2>
      <p>Name: {userData.userInfos.firstName} {userData.userInfos.lastName}</p>
      <p>Age: {userData.userInfos.age}</p>
      <p>Today's Score: {userData.todayScore}</p>
      <p>Calorie Count: {userData.keyData.calorieCount}</p>
      {/* Ostali prikazi informacija o korisniku */}
    </div>
  );
};

export default UserProfile;
