import React, { useEffect, useState } from 'react';
import SessionChart from '../SessionChart/SessionChart';
import PerformanceChart from '../PerformanceChart/PerformanceChart';
import axios from 'axios';

const Profile = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [performance, setPerformance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userDataResponse = await axios.get(`http://localhost:3000/user/${userId}`);
        const userData = userDataResponse.data.data;

        const activityResponse = await axios.get(`http://localhost:3000/user/${userId}/activity`);
        const activityData = activityResponse.data.data;


        const performanceResponse = await axios.get(`http://localhost:3000/user/${userId}/performance`);
        const performanceData = performanceResponse.data.data;

        setUser(userData.userInfos);
        setSessions(activityData.sessions);
        setPerformance(performanceData.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();

    return () => {
      // Cleanup function if needed
    };
  }, [userId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Bonjour {user.firstName}</h1>
      <SessionChart data={sessions} />
      <PerformanceChart data={performance} />
    </div>
  );
};

export default Profile;
