import React, { useEffect, useState } from 'react';
import {
  fetchUserData,
  fetchUserActivity,
  fetchUserAverageSessions,
  fetchUserPerformance,
} from '../fetchFunctions/fetchFunctions';
import ActivityChart from '../recharts/ActivityChart';
import AverageSessionsChart from './AverageSessionsChart';
import PerformanceChart from './PerformanceChart';

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const userData = await fetchUserData(userId);
      const activityData = await fetchUserActivity(userId);
      const averageSessionsData = await fetchUserAverageSessions(userId);
      const performanceData = await fetchUserPerformance(userId);

      setUserData(userData);
      setUserActivity(activityData.sessions); // Ovde se prilagoduvate kako so sodrzinata na podatocite
      setUserAverageSessions(averageSessionsData.sessions); // Prilagodete gi spored vashta implementacija
      setUserPerformance(performanceData.performance); // Isto taka tuka
    };

    fetchData();
  }, [userId]);

  if (!userData || !userActivity || !userAverageSessions || !userPerformance) return <div>Loading...</div>;

  return (
    <div>
      <h1>{userData.userInfos.firstName}</h1>
      <p>Age: {userData.userInfos.age}</p>
      <h2>Activity</h2>
      <ActivityChart data={userActivity} />
      <h2>Average Sessions</h2>
      <AverageSessionsChart data={userAverageSessions} />
      <h2>Performance</h2>
      <PerformanceChart data={userPerformance} />
    </div>
  );
};

export default Profile;
