import React, { useState, useEffect } from 'react';
import './Profile.css'; // Import your CSS styles here
import ActivityChart from '../charts/ActivityChart/ActivityChart'; // Example import for ActivityChart component
import AverageSessionsChart from '../charts/AverageSessionsChart/AverageSessionsChart'; // Example import for AverageSessionsChart component
import NutritionalInfo from '../charts/NutritionalInfo/NutritionalInfo'; // Example import for NutritionalInfo component
import RadarChart from '../charts/PerformanceChart/RadarChart'; // Example import for RadarChart component
import ScoreChart from '../charts/ScoreChart/ScoreChart'; // Example import for ScoreChart component

// Assuming this is your mock data structure
import mockData from '../../mockData.json';

// Define emptyUserData if necessary
const emptyUserData = {}; // Define an empty object or adjust based on your needs

const Profile = ({ userId }) => {
  // State hooks for storing user data
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);

  // useEffect to simulate fetching user data
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Simulating fetching data from an API or using mock data
        // Replace with your actual fetching logic
        await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate loading delay

        // Example mock data usage
        const mockUserData = {
          userInfos: {
            firstName: 'Cecilia',
          },
          keyData: {
            calorieCount: 2500,
            proteinCount: 90,
            carbohydrateCount: 150,
            lipidCount: 120,
          },
          score: 85,
        };

        setUserData(mockUserData);
        setUserActivity(mockData.USER_ACTIVITY.find(activity => activity.userId === parseInt(userId)));
        setUserAverageSessions(mockData.USER_AVERAGE_SESSIONS.find(session => session.userId === parseInt(userId)));
        setUserPerformance(mockData.USER_PERFORMANCE.find(performance => performance.userId === parseInt(userId)));
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);

  // If data is still loading, display a loading message
  if (!userData || !userActivity || !userAverageSessions || !userPerformance) {
    return <div>Loading...</div>;
  }

  // Destructure values for easier access in the JSX
  const { firstName } = userData.userInfos;
  const { calorieCount, proteinCount, carbohydrateCount, lipidCount } = userData.keyData;
  const { score } = userData;

  return (
    <div className="profile-container">
      {/* Header section */}
      <header className="header">
        <nav className="nav">
          <ul className="nav-list">
            <li className="nav-item">Accueil</li>
            <li className="nav-item">Profil</li>
            <li className="nav-item">R√©glage</li>
            <li className="nav-item">Communaut√©</li>
            <li className="nav-item">Profil 18</li>
            <li className="nav-item">Swimming</li>
            <li className="nav-item">Meditation</li>
            <li className="nav-item">Weights</li>
            <li className="nav-item">Cycling</li>
          </ul>
        </nav>
      </header>

      {/* Main content */}
      <main className="main-content">
        <div className="greeting">
          <h1>Bonjour {firstName}</h1>
          <p>F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
        </div>

        {/* Daily activity section */}
        <section className="daily-activity">
          <h2>Activit√© quotidienne</h2>
          {/* Replace with your actual daily activity chart component */}
          <ActivityChart data={userActivity.sessions} />
          {/* Example hardcoded dates and values */}
          <div className="activity-table">
            <table>
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Calories br√ªl√©es (kCal)</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>2020-07-01</td>
                  <td>67</td>
                </tr>
                <tr>
                  <td>2020-07-02</td>
                  <td>68</td>
                </tr>
                {/* Add more rows as needed */}
              </tbody>
            </table>
          </div>
        </section>

        {/* Nutrition information section */}
        <section className="nutrition-info">
          <h2>Calories</h2>
          <div className="nutrition-table">
            <table>
              <thead>
                <tr>
                  <th>Calories</th>
                  <th>Proteins</th>
                  <th>Carbohydrates</th>
                  <th>Fat</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{calorieCount} kCal</td>
                  <td>{proteinCount} g</td>
                  <td>{carbohydrateCount} g</td>
                  <td>{lipidCount} g</td>
                </tr>
              </tbody>
            </table>
          </div>
        </section>
      </main>

      {/* Footer section */}
      <footer className="footer">
        <p>Copyright, SportSee 2020</p>
      </footer>
    </div>
  );
};

export default Profile;
