import React, { useState } from 'react';
import './header.css';
import logo from '../../images/logo/logo.png';

const Header = ({ userId, onUserChange }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleToggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleUserChange = (e) => {
    onUserChange(e.target.value);
    setDropdownOpen(false); // Zatvoranje na dropdown menito po izbor na korisnik
  };

  return (
    <nav className="horizontal-nav">
      <ul>
        <li><img src={logo} alt="logo" className="logo" /></li>
        <li>Accueil</li>
        <li className="profile-dropdown">
          <div className="profile-title" onClick={handleToggleDropdown}>
            Profil
            <i className={`dropdown-icon ${dropdownOpen ? 'open' : ''}`} />
          </div>
          {dropdownOpen && (
            <select value={userId} onChange={handleUserChange}>
              <option value="18">User 18</option>
              <option value="12">User 12</option>
            </select>
          )}
        </li>
        <li>Réglage</li>
        <li>Communauté</li>
      </ul>
    </nav>
  );
};

export default Header;
