import React, { useEffect, useRef } from 'react';
import { LineChart, Line, XAxis, Tooltip } from 'recharts';
import './AverageSessionsChart.css';

const daysOfWeek = ['L', 'M', 'M', 'J', 'V', 'S', 'D'];

const AverageSessionsChart = ({ data, title }) => {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current?.getCanvasElement().getContext('2d');

    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    const sortedData = data.slice().sort((a, b) => a.day - b.day); // Ensure sorting does not mutate original data

    chartInstanceRef.current = new LineChart(ctx, {
      data: {
        labels: sortedData.map((d) => daysOfWeek[d.day - 1]),
        datasets: [{
          data: sortedData.map((d) => d.sessionLength),
          backgroundColor: 'rgba(255, 1, 1, 0.7)',
          borderColor: 'rgba(255, 1, 1, 0.7)',
          borderWidth: 1,
        }],
      },
      options: {
        scales: {
          x: {
            grid: {
              color: 'rgba(255, 255, 255, 0.1)',
            },
            ticks: {
              color: 'rgba(255, 255, 255, 0.6)',
            },
            display: true,
            title: {
              display: true,
              text: 'Days',
              color: 'rgba(255, 255, 255, 1)',
              font: {
                size: 16,
                family: "'Roboto', sans-serif",
              },
            },
          },
          y: {
            grid: {
              color: 'rgba(255, 255, 255, 0.1)',
            },
            ticks: {
              color: 'rgba(255, 255, 255, 0.6)',
            },
            display: true,
            title: {
              display: true,
              text: 'Session Length (min)',
              color: 'rgba(255, 255, 255, 1)',
              font: {
                size: 16,
                family: "'Roboto', sans-serif",
              },
            },
          },
        },
        responsive: false, // Disable responsiveness
        maintainAspectRatio: true, // Maintain square aspect ratio
        aspectRatio: 1, // Aspect ratio 1:1 (square)
      },
    });

    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [data, title]);

  return (
    <div className="chart-container-average">
      <div className="chart-header-average">
        <h2>{title}</h2>
      </div>
      <div className="chart-wrapper-average">
        <canvas ref={chartRef} />
      </div>
    </div>
  );
};

export default AverageSessionsChart;
