import React from 'react';
import { BarChart, Bar, CartesianGrid, Tooltip, Legend, XAxis, YAxis, ResponsiveContainer } from 'recharts';
import './ActivityChart.css';

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${payload[0].value}kg`}</p>
        <p className="label">{`${payload[1].value}kCal`}</p>
      </div>
    );
  }

  return null;
};

const ActivityChart = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>Loading...</div>; // Gérer l'état de chargement ou le scénario de données vides
  }

  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
        <Legend 
          verticalAlign="middle" 
          align="right" 
          iconType="circle" 
          formatter={(value, entry) => (
            <span style={{ color: entry.color }}>
              {value === 'kilogram' ? 'Poids (kg)' : 'Calories brûlées (kCal)'}
            </span>
          )}
        />
      </div>
      <div className="chart">
        <ResponsiveContainer width="100%" height={400}>
          <BarChart
            data={data}
            margin={{ top: 20, right: 30, left: 20, bottom: 30 }}
          >
            <CartesianGrid strokeDasharray="3 3" vertical={false} />
            <XAxis 
              dataKey="day" 
              label={{ value: 'Jour', position: 'insideBottom', offset: -5 }} 
              tick={{ fontSize: 12 }} 
              tickLine={false}
              stroke="#DEDEDE"
            />
            <YAxis
              yAxisId="left"
              dataKey="kilogram"
              domain={[69, 71]}
              allowDecimals={false}
              label={{ value: 'kg', angle: -90, position: 'insideRight', offset: -10 }}
              tick={{ fontSize: 12 }}
              tickLine={false}
              axisLine={false}
              stroke="#DEDEDE"
            />
            <YAxis
              yAxisId="right"
              dataKey="calories"
              orientation="right"
              allowDecimals={false}
              hide
            />
            <Tooltip content={<CustomTooltip />} />
            <Bar 
              yAxisId="left" 
              dataKey="kilogram" 
              fill="black" 
              name="Poids (kg)" 
              barSize={10} 
              radius={[10, 10, 0, 0]} 
            />
            <Bar 
              yAxisId="right" 
              dataKey="calories" 
              fill="red" 
              name="Calories brûlées (kCal)" 
              barSize={10} 
              radius={[10, 10, 0, 0]} 
            />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ActivityChart;
