import React from 'react';
import './ScoreChart.css';
import Chart from 'chart.js/auto';

const ScoreChart = ({ todayScore, score }) => {
  const chartRef = React.useRef(null);
  const chartInstanceRef = React.useRef(null);
  const textRef = React.useRef(null);

  React.useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    // Calculate remaining and achieved scores
    const remainingScore = 1 - score;
    const todayRemainingScore = 1 - todayScore;

    chartInstanceRef.current = new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: ['Remaining', 'Achieved'],
        datasets: [{
          data: [remainingScore * 100, score * 100],
          backgroundColor: ['rgba(255, 255, 255, 0.2)', 'rgba(255, 0, 0, 1)'],
          borderColor: ['rgba(255, 255, 255, 0.2)', 'rgba(255, 0, 0, 1)'],
          borderWidth: 1
        }, {
          data: [todayRemainingScore * 100, todayScore * 100],
          backgroundColor: ['rgba(255, 255, 255, 0.5)', 'rgba(0, 255, 0, 1)'],
          borderColor: ['rgba(255, 255, 255, 0.5)', 'rgba(0, 255, 0, 1)'],
          borderWidth: 1
        }]
      },
      options: {
        cutout: '80%', 
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            enabled: false
          },
          title: {
            display: true,
            text: 'Score',
            font: { weight: 'bold', size: 16 },
            position: 'top',
            padding: 10, 
            lineHeight: 1.5, 
            align: 'start', 
            anchor: 'start',
            offset: { 
              y: 60 
            }
          }
        }
      }
    });

    if (textRef.current) {
      textRef.current.innerHTML = `
        <div>
          <span style="color: black; white-space: nowrap;">${score * 100}%</span>
          <span style="color: rgba(116, 121, 140, 1)"> od vaš cilj</span>
        </div>
        <div>
          <span style="color: black; white-space: nowrap;">${todayScore * 100}%</span>
          <span style="color: rgba(116, 121, 140, 1)"> danas</span>
        </div>
      `;
    }

    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [todayScore, score]);

  return (
    <div style={{ position: 'relative' }}>
      <canvas ref={chartRef} style={{ color: 'black', backgroundColor: 'rgba(251, 251, 251, 1)'}} />
      <div ref={textRef} style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -30%)',
        fontFamily: 'Roboto, sans-serif',
        fontSize: '16px',
        textAlign: 'center',
        lineHeight: '1.2',
      }}></div>
    </div>
  );
};

export default ScoreChart;
