import React from 'react';
import { BarChart, Bar, CartesianGrid, Tooltip, Legend } from 'recharts';
import './ActivityChart.css';
import XAxis from '../../Axis/XAxis';
import YAxis from '../../Axis/YAxis';

const ActivityChart = ({ data }) => {
  return (
    <div className="chart-container">
      <div className="chart-header">
        <h2>Activité quotidienne</h2>
      </div>
      <div className="chart">
        <BarChart width={835} height={320} data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="day" />
          <YAxis />
          <Tooltip />
          <Legend verticalAlign="top" height={36} />
          <Bar dataKey="kilogram" stackId="a" fill="black" name="Poids (kg)" barSize={7} xAxisId="day" />
          <Bar dataKey="calories" stackId="b" fill="red" name="Calories brûlées (kCal)" barSize={7} xAxisId="day" />
        </BarChart>
      </div>
    </div>
  );
};

export default ActivityChart;
