const BASE_URL = 'http://localhost:3000/api/data';

async function checkResponse(response) {
  if (!response.ok) {
    const errorText = await response.text();
    console.error(`Network response was not ok. Status: ${response.status}. Message: ${errorText}`);
    throw new Error(`Network response was not ok. Status: ${response.status}`);
  }

  // Check if the response is JSON
  const contentType = response.headers.get('content-type');
  if (contentType && contentType.indexOf('application/json') !== -1) {
    return response.json();
  } else {
    const textResponse = await response.text();
    console.error('Expected JSON, but received:', textResponse);
    throw new Error('Received non-JSON response');
  }
}
import { fetchUserData, fetchUserActivity, fetchUserAverageSessions, fetchUserPerformance } from './api'; // Заменете со точната патека до вашиот API модул

/**
 * Fetch activity data from API or local JSON file
 * @param {string} id
 * @param {boolean} setup
 * @returns {object}
 */
export const getActivityData = async (id, setup) => {
  if (setup) {
    try {
      const data = await fetchUserActivity(id);
      return data;
    } catch (error) {
      console.error('Error fetching activity data:', error);
      throw error;
    }
  } else {
    try {
      const response = await fetch(`../data/${id}/activity.json`);
      const data = await checkResponse(response);
      return data;
    } catch (error) {
      console.error('Error fetching local activity data:', error);
      throw error;
    }
  }
};

/**
 * Fetch average session data from API or local JSON file
 * @param {String} id
 * @param {Boolean} setup
 * @returns {Object}
 */
export const getAverageData = async (id, setup) => {
  if (setup) {
    try {
      const data = await fetchUserAverageSessions(id);
      return data;
    } catch (error) {
      console.error('Error fetching average session data:', error);
      throw error;
    }
  } else {
    try {
      const response = await fetch(`../data/${id}/average.json`);
      const data = await checkResponse(response);
      return data;
    } catch (error) {
      console.error('Error fetching local average session data:', error);
      throw error;
    }
  }
};

/**
 * Fetch performance data from API or local JSON file
 * @param {String} id
 * @param {Boolean} setup
 * @returns {Object}
 */
export const getPerformanceData = async (id, setup) => {
  if (setup) {
    try {
      const data = await fetchUserPerformance(id);
      return data;
    } catch (error) {
      console.error('Error fetching performance data:', error);
      throw error;
    }
  } else {
    try {
      const response = await fetch(`../data/${id}/performance.json`);
      const data = await checkResponse(response);
      return data;
    } catch (error) {
      console.error('Error fetching local performance data:', error);
      throw error;
    }
  }
};


function mapUserData(userData) {
  return {
    id: userData.id,
    userInfos: {
      firstName: userData.firstName,
      lastName: userData.lastName,
    },
    keyData: {
      calorieCount: userData.calorieCount,
      proteinCount: userData.proteinCount,
      carbohydrateCount: userData.carbohydrateCount,
      lipidCount: userData.lipidCount,
    },
    score: userData.score,
  };
}

function mapUserActivity(userActivity) {
  return {
    userId: userActivity.userId,
    sessions: userActivity.map(session => ({
      day: session.created_at,
      sessionLength: session.sessionLength,  // Assuming `sessionLength` is a valid field
    })),
  };
}

function mapUserAverageSessions(userAverageSessions) {
  return {
    userId: userAverageSessions.userId,
    sessions: userAverageSessions.sessions.map(session => ({
      day: session.day,
      averageDuration: session.sessionLength,
    })),
  };
}

function mapUserPerformance(userPerformance) {
  return {
    userId: userPerformance.userId,
    data: userPerformance.data.map(item => ({
      kind: item.kind,
      value: item.value,
    })),
  };
}
