import React, { useEffect, useState } from 'react';
import './Profile.css';
import ActivityChart from '../charts/ActivityChart/ActivityChart';
import { fetchUserData, fetchUserActivity } from '../fetchFunctions/fetchFunctions';

const Profile = ({ userId }) => {
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [averageData, setAverageData] = useState(null); // Dodali smo state za proseƒçne vrednosti

  useEffect(() => {
    const fetchData = async () => {
      const data1 = await fetchUserData(userId);
      const data2 = await fetchUserActivity(userId);

      setUserData(data1);
      setUserActivity(data2);

      // Izraƒçunavanje proseƒçnih vrednosti
      const avgKilogram = calculateAverage(data2.sessions, 'kilogram');
      const avgCalories = calculateAverage(data2.sessions, 'calories');
      setAverageData({ avgKilogram, avgCalories });
    };

    fetchData();
  }, [userId]);

  const calculateAverage = (sessions, key) => {
    if (!sessions || sessions.length === 0) return 0;

    const total = sessions.reduce((acc, session) => acc + session[key], 0);
    return total / sessions.length;
  };

  if (!userData || !userActivity || !averageData) return <div>Loading...</div>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>Bonjour <span className="user-name">{userData.userInfos.firstName}</span></h1>
        <p>F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
      </div>

      <div className="section">
        <ActivityChart data={userActivity.sessions} />
        <div className="average-data">
          <h3>Proseƒçni podaci</h3>
          <p>Proseƒçno te≈æina: {averageData.avgKilogram.toFixed(2)} kg</p>
          <p>Proseƒçne kalorije: {averageData.avgCalories.toFixed(2)} kCal</p>
        </div>
      </div>
   
    </div>
  );
};

export default Profile;
