  try {
    const response = await fetch(`${BASE_URL}/userData/${userId}`);
    if (!response.ok) {
      throw new Error('Failed to fetch user data');
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Error fetching user data", error);
    return null;
  }
};

