import React from 'react';
import './VerticalNav.css';
import swimmingIcon from '../../images/icons/swimming.png';
import meditationIcon from '../../images/icons/meditation.png';
import weightsIcon from '../../images/icons/weights.png';
import cyclingIcon from '../../images/icons/cycling.png';

const VerticalNav = ({ onUserChange }) => {
  const handleSwimmingClick = () => {
    console.log("Swimming button clicked");
    // Add functionality for swimming button click if needed
  };

  const handleMeditationClick = () => {
    console.log("Meditation button clicked");
    // Add functionality for meditation button click if needed
  };

  const handleWeightsClick = () => {
    console.log("Weights button clicked");
    // Add functionality for weights button click if needed
  };

  const handleCyclingClick = () => {
    console.log("Cycling button clicked");
    // Add functionality for cycling button click if needed
  };

  const handleUserClick = (userId) => {
    onUserChange(userId); // Call the callback to change the user in App
  };

  return (
    <div>
      <nav className="vertical-nav">
        <ul>
          <li>
            <button className="nav-button" onClick={handleSwimmingClick}>
              <img src={swimmingIcon} alt="Swimming" className="nav-icon" />
            </button>
          </li>
          <li>
            <button className="nav-button" onClick={handleMeditationClick}>
              <img src={meditationIcon} alt="Meditation" className="nav-icon" />
            </button>
          </li>
          <li>
            <button className="nav-button" onClick={handleWeightsClick}>
              <img src={weightsIcon} alt="Weights" className="nav-icon" />
            </button>
          </li>
          <li>
            <button className="nav-button" onClick={handleCyclingClick}>
              <img src={cyclingIcon} alt="Cycling" className="nav-icon" />
            </button>
          </li>
          <li onClick={() => handleUserClick(18)}>User 18</li>
          <li onClick={() => handleUserClick(12)}>User 12</li>
        </ul>
        <p className="copyright">Copyright, SportSee 2020</p>
      </nav>
    </div>
  );
};

export default VerticalNav;
