import React, { useEffect, useState } from 'react';
import { fetchUserData } from '../../services/api';
import SessionChart from '../SessionChart/SessionChart';
import CaloriesChart from '../CaloriesChart/CaloriesChart';
import { formatSessionData, formatCaloriesData } from '../../uttils/dataFormatter';

const Profile = () => {
  const [sessions, setSessions] = useState([]);
  const [calories, setCalories] = useState([]);
  const [user, setUser] = useState(null); // Dodajemo stanje za korisnika

  useEffect(() => {
    async function getData() {
      try {
        const userData = await fetchUserData();
        setUser(userData.userInfos.firstName); // Postavljanje imena korisnika u stanje
        setSessions(formatSessionData(userData.sessions));
        setCalories(formatCaloriesData(userData.calories));
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    }
    getData();
  }, []);

  return (
    <div>
      <h1>Bonjour {user}</h1> {/* Prikazujemo ime korisnika */}
      <SessionChart data={sessions} />
      <CaloriesChart data={calories} />
    </div>
  );
};

export default Profile;
